import React, { useState, useEffect } from 'react';
import TaskService from './TaskService';
import './App.css';

function App() {
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState('');
    const [deadline, setDeadline] = useState('');

    useEffect(() => {
        loadTasks();
    }, []);

    const loadTasks = () => {
        TaskService.getTasks().then(response => {
            setTasks(response.data);
        });
    };

    const addTask = () => {
        if (newTask.length <= 10) {
            alert('Task description must be longer than 10 characters.');
            return;
        }

        const task = {
            description: newTask,
            isDone: false,
            deadline: deadline ? new Date(deadline) : null
        };

        TaskService.createTask(task).then(() => {
            loadTasks();
            setNewTask('');
            setDeadline('');
        });
    };

    const deleteTask = (id) => {
        TaskService.deleteTask(id).then(() => loadTasks());
    };

    const markDone = (task) => {
        task.isDone = true;
        TaskService.updateTask(task.id, task).then(() => loadTasks());
    };

    return (
        <div className="App">
            <h1>ToDo App</h1>
            <input
                type="text"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                placeholder="New task..."
            />
            <input
                type="date"
                value={deadline}
                onChange={(e) => setDeadline(e.target.value)}
            />
            <button onClick={addTask}>Add Task</button>

            <table>
                <thead>
                    <tr>
                        <th>Description</th>
                        <th>Deadline</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {tasks.map((task) => (
                        <tr key={task.id} style={{ color: task.deadline && new Date(task.deadline) < new Date() && !task.isDone ? 'red' : 'black' }}>
                            <td>{task.description}</td>
                            <td>{task.deadline ? new Date(task.deadline).toLocaleDateString() : 'No Deadline'}</td>
                            <td>{task.isDone ? 'Done' : 'Pending'}</td>
                            <td>
                                <button onClick={() => markDone(task)}>Mark as Done</button>
                                <button onClick={() => deleteTask(task.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default App;


