import React, { useState, useEffect } from "react";
import TodoForm from "./TodoForm";
import Todo from "./Todo";
import EditTodoForm from "./EditTodoForm";
import { getTasks, addTask, updateTask, deleteTask } from "/api"; // Import API functions

const ToDoWrapper = () => {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState("all");

  // Fetch tasks from the database on component mount
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const response = await getTasks();
        setTodos(response.data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      }
    };

    fetchTodos();
  }, []);

  const addTodo = async (todo) => {
    try {
      const response = await addTask(todo);
      setTodos([...todos, response.data]);
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const toggleComplete = async (id) => {
    const todoToToggle = todos.find((todo) => todo.id === id);
    if (todoToToggle) {
      const updatedTodo = {
        ...todoToToggle,
        completed: !todoToToggle.completed,
      };
      try {
        await updateTask(id, updatedTodo);
        setTodos(
          todos.map((todo) =>
            todo.id === id ? { ...todo, completed: !todo.completed } : todo
          )
        );
      } catch (error) {
        console.error("Error updating task:", error);
      }
    }
  };

  const deleteTodo = async (id) => {
    try {
      await deleteTask(id);
      setTodos(todos.filter((todo) => todo.id !== id));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const editTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo
      )
    );
  };

  const editTask = async ({ task, deadline, id }) => {
    try {
      const updatedTask = { task, deadline, id, completed: false };
      await updateTask(id, updatedTask);
      setTodos(
        todos.map((todo) =>
          todo.id === id
            ? { ...todo, task, deadline, isEditing: !todo.isEditing }
            : todo
        )
      );
    } catch (error) {
      console.error("Error editing task:", error);
    }
  };

  const filteredTodos = () => {
    switch (filter) {
      case "completed":
        return todos.filter((todo) => todo.completed);
      case "incomplete":
        return todos.filter((todo) => !todo.completed);
      default:
        return todos;
    }
  };

  return (
    <div className="TodoWrapper">
      <h1>Get Things Done!</h1>
      <TodoForm addTodo={addTodo} />
      <div className="filters">
        <button onClick={() => setFilter("all")}>All</button>
        <button onClick={() => setFilter("completed")}>Completed</button>
        <button onClick={() => setFilter("incomplete")}>Incomplete</button>
      </div>
      <table className="todo-table">
        <thead>
          <tr>
            <th>Task</th>
            <th>Deadline</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredTodos().map((todo) =>
            todo.isEditing ? (
              <EditTodoForm editTodo={editTask} task={todo} key={todo.id} />
            ) : (
              <Todo
                task={todo}
                key={todo.id}
                toggleComplete={toggleComplete}
                deleteTodo={deleteTodo}
                editTodo={editTodo}
              />
            )
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ToDoWrapper;
